import { createLaunchpadSDK } from '@gala-chain/launchpad-sdk';

async function viewCurrentLeaderboard() {
  const sdk = createLaunchpadSDK({ wallet: 'your-private-key' });

  // Fetch current season leaderboard directly
  const leaderboard = await sdk.fetchCurrentDexLeaderboard();

  if (leaderboard) {
    console.log('Current Season:', leaderboard.season.name);
    console.log('Total Participants:', leaderboard.entries.length);

    // Display top 10 traders
