      console.log(`   XP: ${entry.xp}, Volume: $${entry.totalVolume}`);

      // Display mastery titles
      if (entry.masteryTitles.length > 0) {
        console.log(`   Titles: ${entry.masteryTitles.join(', ')}`);
      }
    });

    // Find your own position
    const myAddress = sdk.getAddress();
    const myPosition = leaderboard.entries.findIndex(e => e.wallet === myAddress);

    if (myPosition !== -1) {
      const myEntry = leaderboard.entries[myPosition];
      console.log(`\nYour position: #${myPosition + 1}`);
      console.log(`XP: ${myEntry.xp}, Volume: $${myEntry.totalVolume}`);
    } else {
      console.log('\nYou have not participated in this season yet.');
    }
  } else {
    console.log('No active season currently running.');
  }
}
