  tokenName: 'mytoken',
  role: MODERATOR_ROLE.MODERATOR,
  description: 'John - Friday stream moderator.',
  expiresAt: '2025-12-31T23:59:59Z', // Optional expiration
});

// Share this URL with the intended moderator

