  const top5Ownership = distribution.holders
    .sort((a, b) => compareAmounts(b.balance, a.balance) > 0 ? 1 : -1)
    .slice(0, 5)
    .reduce((sum, holder) => sum + holder.percentage, 0);

  // Find specific holder
