examples.connectionSetup:
import { StreamWebSocketService } from '@gala-chain/launchpad-sdk';

const wsService = new StreamWebSocketService({
  url: 'wss://stream.gala.com',
  authToken: 'your-jwt-token'
});

await wsService.connect();

examples.subscriptionPattern:
// Subscribe to trade events for a token
await wsService.subscribeToStream('mytoken', {
  onTradeExecuted: (event) => {
    console.log('Trade executed:', event.tokenName, event.tradeType, event.outputAmount);
  }
});

// Subscribe to moderator changes
wsService.subscribeModeratorAdded('mytoken', (event) => {
  console.log('New moderator:', event.userAddress, 'Role:', event.role);
});
